decisionRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;
    const complicationsBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "Diagnosis of sepsis"
    });

      complicationsBuilder
        .addComplication("Respiratory rate >60/min")
        .when.valueInEncounter("Child Respiratory Rate").is.greaterThan(60);

      complicationsBuilder
        .addComplication("Yellowing of skin/eye")
        .when.valueInEncounter("Yellowing of skin/eye").containsAnswerConceptName('Yes');

      complicationsBuilder
        .addComplication("Skin - Redness/crack in skin fold")
        .when.valueInEncounter("Skin - Redness/crack in skin fold").containsAnswerConceptName('Yes');

      complicationsBuilder
        .addComplication("Skin - pus filled rashes/boils")
        .when.valueInEncounter("Skin - pus filled rashes/boils").containsAnswerConceptName('Yes');

     complicationsBuilder
        .addComplication("High Temperature")
        .when.valueInEncounter("Temperature of newborn (celcius)").is.greaterThan(37.8);

    complicationsBuilder
        .addComplication("Low Temperature")
        .when.valueInEncounter("Temperature of newborn (celcius)").is.lessThan(36.1);


    //Look for any danger signs of sepsis within 6 hours
    //Look for any danger signs of sepsis after 6 hours

     //Weak and flaccid legs and hands

    complicationsBuilder.addComplication("Weak and flaccid legs and hands").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Weak and flaccid legs and hands').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Weak and flaccid legs and hands');

    //Weak cry or stopped crying

    complicationsBuilder.addComplication("Weak cry or stopped crying").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Weak cry or stopped crying').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Weak cry or stopped crying');


    //Difficulty in breathing
     complicationsBuilder.addComplication("Difficulty in breathing").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Difficulty in breathing').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Difficulty in breathing');


    //Reduced or stopped breastfeeding
     complicationsBuilder.addComplication("Reduced or stopped breastfeeding").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Reduced or stopped breastfeeding').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Reduced or stopped breastfeeding');


    //Distended abdomen of mother says baby vomits frequently
     complicationsBuilder.addComplication("Distended abdomen of mother says baby vomits frequently").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Distended abdomen of mother says baby vomits frequently').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Distended abdomen of mother says baby vomits frequently');


    //Mother says baby is cold or temp. is more than 37.2 DC
     complicationsBuilder.addComplication("Mother says baby is cold or temp. is more than 37.2 DC").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Mother says baby is cold or temp. is more than 37.2 DC').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Mother says baby is cold or temp. is more than 37.2 DC');


    //Chest indrawing
     complicationsBuilder.addComplication("Chest indrawing").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Chest indrawing').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Chest indrawing');


    //Pus in or around the umbilicus
     complicationsBuilder.addComplication("Pus in or around the umbilicus").when.valueInEncounter("Look for any danger signs of sepsis within 6 hours").containsAnswerConceptName('Pus in or around the umbilicus').or.when.valueInEncounter("Look for any danger signs of sepsis after 6 hours").containsAnswerConceptName('Pus in or around the umbilicus');


    decisions.encounterDecisions.push(complicationsBuilder.getComplications());
    return decisions;
};

visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });
  const dateOfBirth = programEncounter.individual.dateOfBirth;
   console.log('dateOfBirth ====>',dateOfBirth);

  const encounterSchedulePNC = [
        {"name": "PNC 2", "earliest": 1, "max": 4},
        {"name": "PNC 3", "earliest": 2, "max": 5},
        {"name": "PNC 4", "earliest": 6, "max": 9},
        {"name": "PNC 5", "earliest": 14, "max": 17},
        {"name": "PNC 6", "earliest": 28, "max": 31},
        {"name": "PNC 7", "earliest": 41, "max": 44}
];

  const visitDate = programEncounter.encounterDateTime; 
  const deathStatus=programEncounter.getObservationReadableValue('Whether baby die');
  console.log('deathStatus ====>',deathStatus);
   var schedule = _.chain(encounterSchedulePNC).filter(e => imports.moment(visitDate).isSameOrBefore(imports.moment(dateOfBirth).add(e.earliest, 'days'), 'date') === true)
            .filter(e => (programEncounter.programEnrolment.hasEncounter('Child PNC', e.name)) === false)
            .first()
            .value();

  if (!_.isEmpty(schedule) && deathStatus == 'No') {
  scheduleBuilder
    .add({
      name: schedule.name,
      encounterType: "Child PNC",
      earliestDate:  imports.moment(dateOfBirth).add(schedule.earliest, 'days').toDate() ,
      maxDate: imports.moment(dateOfBirth).add(schedule.max, 'days').toDate() 
    });
  }

  const referralAns =programEncounter.getObservationReadableValue('Referral needed to newborn');
  const earliestDate=imports.moment(programEncounter.encounterDateTime).add(3,'days').toDate();

  if(referralAns)
  scheduleBuilder
    .add({
      name: "Referral Status-1",
      encounterType: "Referral Status",
      earliestDate: earliestDate,
      maxDate: imports.moment(earliestDate).add(2, 'days').toDate()
    }).whenItem(referralAns == 'Yes')
    .is.truthy;

  return scheduleBuilder.getAll();
};
