visitScheduleRule:
//SAMPLE RULE EXAMPLE
"use strict";
({ params, imports }) => {
  const encounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    encounter
  });
  //add new visit schedule object to scheduleBuilder
   let visitDate = imports.moment(encounter.earliestVisitDateTime).add(1, 'months').toDate();
  scheduleBuilder
                .add({
                    name: "SHG monthly data collection",
                    encounterType: "SHG monthly data collection",
                    earliestDate: visitDate,
                    maxDate: imports.moment(visitDate).add(3, 'days').toDate()
                });
  return scheduleBuilder.getAll();
};
