"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;
    const complicationsBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "High Risk Conditions"
    });
    //Height is less than 140 cm
     complicationsBuilder
        .addComplication("Height is less than 140 cm")
        .when.valueInEncounter("Height")
        .is.lessThan(140);
    //Weight is less than 35 kg
     complicationsBuilder
        .addComplication("Weight is less than 35 kg")
        .when.valueInEncounter("Weight")
        .is.lessThan(35);
    //High Blood Pressure
     complicationsBuilder
        .addComplication("High Blood Pressure")
        .when.valueInEncounter("Blood Pressure (Diastolic)")
        .is.greaterThanOrEqualTo(90)
        .or.when.valueInEncounter("Blood Pressure (systolic)")
        .is.greaterThanOrEqualTo(140);
    complicationsBuilder
        .addComplication("Oedema")
        .when.valueInEncounter("Oedema")
        .containsAnyAnswerConceptName("Face","Vulval","Entire body");
    complicationsBuilder
        .addComplication("Hydroamniosis")
        .when.valueInEncounter("Abdomen check")
        .containsAnswerConceptName("Hydroamniosis");
  complicationsBuilder
        .addComplication("Twins")
        .when.valueInEncounter("Abdomen check")
        .containsAnswerConceptName("Twins");
        //Foetal Position
  complicationsBuilder
        .addComplication("Foetal Position is not normal")
        .when.valueInEncounter("Position")
        .containsAnswerConceptName("Transverse","Breach")
        .or.when.valueInEncounter("Position of baby 2")
        .containsAnswerConceptName("Transverse","Breach");
      //FHS - Not Audible
    complicationsBuilder
        .addComplication("FHS - Not Audible")
        .when.valueInEncounter("FHS")
        .containsAnswerConceptName("Not audible/Can't locate")
        .or.when.valueInEncounter("FHS of baby 2")
        .containsAnswerConceptName("Not audible/Can't locate");
     complicationsBuilder
        .addComplication("No foetal movement")
        .when.valueInEncounter("Foetus movement")
        .containsAnyAnswerConceptName("No foetal movement")
        .or.when.valueInEncounter("Foetal movement baby 2")
        .containsAnyAnswerConceptName("No foetal movement");

     //Sickle prep
    complicationsBuilder
        .addComplication("Sickle prep")
        .when.valueInEncounter("Sickle prep")
        .containsAnswerConceptName("Positive");
    complicationsBuilder
        .addComplication("Rh negative blood group")
        .when.valueInEncounter("Blood group")
        .containsAnyAnswerConceptName("AB-", "O-", "A-", "B-");
    complicationsBuilder
        .addComplication("Urine Albumin Present")
        .when.valueInEncounter("Urine Albumin")
        .containsAnyAnswerConceptName("1+","2+","3+","4+");
    complicationsBuilder
        .addComplication("Urine sugar present")
        .when.valueInEncounter("Urine sugar")
        .containsAnyAnswerConceptName("1+","2+","3+","4+");
    complicationsBuilder
        .addComplication("Low Haemoglobin")
        .when.valueInEncounter("Hb")
        .is.lessThan(7);

     complicationsBuilder
