visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });

const ifReferral = programEncounter.getObservationReadableValue('Does woman require referral?');
// console.log('ifReferral',ifReferral);
const referralReason = programEncounter.getObservationReadableValue('Referral reason');
// console.log('referralReason',referralReason);
const edd = programEncounter.programEnrolment.getObservationValue('EDD');
// console.log('edd',edd);
const nextANCDate = programEncounter.getObservationValue('Date of next ANC Visit');
// console.log('nextANCDate',nextANCDate);
const labEarliestDate = imports.moment(programEncounter.encounterDateTime).add(4,'days').toDate();
// console.log('labEarliestDate',labEarliestDate);

let randomBloodSugarValue = 0;
let glucoseTestValue = 0;

 randomBloodSugarValue = programEncounter.getObservationValue('Random Blood Sugar (RBS)');
 glucoseTestValue = programEncounter.getObservationValue('Glucose test (75gm Glucose)');

//Lab Investigations 
if((programEncounter.programEnrolment.hasEncounter('Lab Investigations', 'Lab Investigations Results')) === false){
    scheduleBuilder
   .add({
      name: "Lab Investigations Results",
      encounterType: "Lab Investigations",
      earliestDate: labEarliestDate,
      maxDate: imports.moment(labEarliestDate).add(8, 'days').toDate()
    }); 

 }

 if( randomBloodSugarValue > 160 || glucoseTestValue >140)
 if((programEncounter.programEnrolment.hasEncounter('Lab Investigations Results for pregnancy induced diabetes', 'Lab Investigations Results for pregnancy induced diabetes')) === false){
    scheduleBuilder
   .add({
      name: "Lab Investigations Results for pregnancy induced diabetes",
      encounterType: "Lab Investigations Results for pregnancy induced diabetes",
      earliestDate: labEarliestDate,
      maxDate: imports.moment(labEarliestDate).add(8, 'days').toDate()
    }); 
 }


if(imports.moment(edd).isSameOrBefore(imports.moment(nextANCDate), 'date')){
if((programEncounter.programEnrolment.hasEncounter('Delivery', 'Delivery')) === false)
 scheduleBuilder
    .add({
      name: "Delivery",
      encounterType: "Delivery",
      earliestDate: imports.moment(edd).toDate(),
      maxDate: imports.moment(edd).add(0, 'days').toDate()
    });
    }
else{
  scheduleBuilder
    .add({
      name: "ANC Clinic Visit",
      encounterType: "ANC Clinic Visit",
      earliestDate: imports.moment(nextANCDate).toDate(),
      maxDate: imports.moment(nextANCDate).add(8, 'days').toDate()
    }).whenItem(!_.isEmpty(nextANCDate))
    .is.not.truthy;
    }

    if(ifReferral)
    scheduleBuilder
    .add({
      name: "Referral Status-1",
      encounterType: "Referral Status",
      earliestDate: imports.moment(programEncounter.encounterDateTime).add(3, 'days').toDate(),
      maxDate: imports.moment(programEncounter.encounterDateTime).add(5, 'days').toDate()
    }).whenItem(ifReferral == 'Yes')
    .is.truthy;

    if(referralReason)
  scheduleBuilder
    .add({
      name: "USG Report",
      encounterType: "USG Report",
      earliestDate: imports.moment(programEncounter.encounterDateTime).toDate(),
      maxDate: imports.moment(programEncounter.encounterDateTime).add(4, 'days').toDate()
    }).whenItem(_.includes(referralReason,'Sonography'))
    .is.truthy;


  return scheduleBuilder.getAll();
};
