formElements.rule:
'use strict';
({params, imports}) => {
  const programEnrolment = params.entity;
  const formElement = params.formElement;
  const statusBuilder = new imports.rulesConfig.FormElementStatusBuilder({programEnrolment, formElement});

  const diagnosisDate = programEnrolment.getObservationReadableValue('Date of diagnosis');
  let visitDate = programEnrolment.enrolmentDateTime;

 if(imports.moment(diagnosisDate).isAfter(visitDate, 'date'))
 statusBuilder.validationError("Diagnosis Date cannot be after visit date");

  return statusBuilder.build();
};
