"use strict";
({params, imports}) => {
    const programEnrolment = params.entity;
    const formElement = params.formElement;
    let height = programEnrolment.findLatestObservationInEntireEnrolment("Height", programEnrolment);
    let weight = programEnrolment.findObservation("Weight");
    height = height && height.getValue();
    weight = weight && weight.getValue();
    let value = '';
    if (_.isFinite(weight) && _.isFinite(height)) {
        value = imports.common.calculateBMI(weight, height);
    }
    return new imports.rulesConfig.FormElementStatus(formElement.uuid, true, value);
};

"use strict";
