    let value = '';
    waist = waist && waist.getValue();
    hip = hip && hip.getValue();
    console.log('waist',waist);
    console.log('hip',hip);

    if(waist && hip ){
       value = (waist/hip).toFixed(2);
    }

    return new imports.rulesConfig.FormElementStatus(formElement.uuid, true, value);

};

