'use strict';
({params, imports}) => {
  const programEnrolment = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const condition11 = new imports.rulesConfig.RuleCondition({programEnrolment, formElement}).when.valueInEnrolment("26ca2feb-d6b5-4eae-9e3c-b0302646fc73").lessThan(moment().startOf('day').toDate()).matches();

  if(condition11 ){
    validationErrors.push("Date cannot be in the past");  
}

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEnrolment = params.entity;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
        programEnrolment
    });

    const nextVisitDate = programEnrolment.getObservationValue('Next visit date');

    scheduleBuilder
        .add({
            name: "Diabetes Followup",
            encounterType: "Diabetes Followup",
            earliestDate: imports.moment(nextVisitDate).toDate(),
            maxDate: imports.moment(nextVisitDate).add(8, 'days').toDate()
        }).whenItem(!_.isEmpty(nextVisitDate))
        .is.not.truthy;



    //return scheduleBuilder.getAll();
     return scheduleBuilder.getAllUnique("encounterType", true);
};
