'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter, formElement}).when.valueInEncounter("7a1d6893-f96b-4647-b274-bb78fd369185").lessThan(moment().startOf('day').toDate()).matches();

  if(condition11 ){
    validationErrors.push("Date cannot be in the past");  
}

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
        programEncounter
    });

    const hasExitedProgram = programEncounter => programEncounter.programEnrolment.programExitDateTime;
    if (!hasExitedProgram(programEncounter)) {

        const nextVisitDate = programEncounter.findCancelEncounterObservation('Date of next visit').getReadableValue();

        scheduleBuilder
            .add({
                name: "Epilepsy followup",
                encounterType: "Epilepsy followup",
                earliestDate: imports.moment(nextVisitDate).toDate(),
                maxDate: imports.moment(nextVisitDate).add(8, 'days').toDate()
            }).whenItem(!_.isEmpty(nextVisitDate))
            .is.not.truthy;
    }

    return scheduleBuilder.getAll();
};
