visitScheduleRule:
"use strict";
({ params, imports }) => {
    const encounter = params.entity;
    const moment = imports.moment;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({ encounter });


    if (moment(encounter.earliestVisitDateTime).date() === 25) {

        const earliestDate = moment(encounter.earliestVisitDateTime).date(25).add(1, 'month').toDate();
        const maxDate = moment(earliestDate).add(8, 'days').toDate();

        scheduleBuilder.add({
            name: "Monthly Cluster monitoring",
            encounterType: "Monthly Cluster monitoring",
            earliestDate,
            maxDate
        });
    } else if (moment(encounter.earliestVisitDateTime).date() > 25) {
        const earliestDate = moment(encounter.earliestVisitDateTime).add(15, 'days').date(25).toDate();
        const maxDate = moment(earliestDate).add(8, 'days').toDate();
        scheduleBuilder.add({
            name: "Monthly Cluster monitoring",
            encounterType: "Monthly Cluster monitoring",
            earliestDate,
            maxDate
        });
    } else {

        const earliestDate = moment(encounter.earliestVisitDateTime).date(25).add(1,'month').toDate();
        const maxDate = moment(earliestDate).add(8, 'days').toDate();

        scheduleBuilder.add({
            name: "Monthly Cluster monitoring",
            encounterType: "Monthly Cluster monitoring",
            earliestDate,
            maxDate
        });
    }

    return scheduleBuilder.getAll();
};
