"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;
    const moment = imports.moment;
    const _ = imports.lodash;

    function pregnancyInducedMorbidityConditionChecker(answerConceptUUID) {
        return new imports.rulesConfig.RuleCondition({programEncounter})
            .when.valueInEncounter("12d99265-c769-4236-aff5-fcba73976396")
            .containsAnswerConceptName(answerConceptUUID)
            .matches();
    }

    // Function to retrieve high-risk conditions based on specific rules
    function getHighRiskConditions(){

        // ancHighRiskValues contains the high-risk values that can be found in the ANC Encounter
        const ancHighRiskValues = ["Severe anemia", "Moderate anemia", "Mild anemia", "Inadequate Weight Gain during Pregnancy", "Hypertension", "Diabetes/ Gestational Diabetes", "Tuberculosis", "Asthma", "Pre-eclampsia", "Sickle cell anemia", "Thalassemia", "Heart conditions", "Hypothyroidism", "Syphillis", "Other pre-morbidity conditions"];

        const hbValue = programEncounter.getObservationReadableValue("68bc6e51-eb49-4816-b78b-2427bbab8d92");
        // Defining Rule Conditions for each high-risk condition
        const isSevereAnemia = hbValue && hbValue < 7;
        const isModerateAnemia = hbValue && hbValue >= 7 && hbValue < 10;
        const isMideAnemia = hbValue && hbValue >= 10 && hbValue < 11;
        const isHypertension = pregnancyInducedMorbidityConditionChecker("621462ed-23da-4b73-b590-4af8ccf34b45");
        const isDiabetes = pregnancyInducedMorbidityConditionChecker("9f6c206e-f04a-4a2a-b3ae-78edbffb0f62");
        const isTuberculosis = pregnancyInducedMorbidityConditionChecker("34f5f1a8-8a03-47d0-a91b-2a0fe959fc10");
        const isAsthma = pregnancyInducedMorbidityConditionChecker("11228d39-49f1-4a44-9bbf-6c673c208326");
        const isPreeclampsia = pregnancyInducedMorbidityConditionChecker("b95305a1-1831-49b7-a5a6-b4cb458a5d6f");
        const isSickleCellAnemia = pregnancyInducedMorbidityConditionChecker("4b0c2486-20ac-458b-b89a-28603715aeb2");
        const isThalassemia = pregnancyInducedMorbidityConditionChecker("83168c77-2a4d-4650-9ead-5c42f1c7f0c4");
        const isHeartConditions = pregnancyInducedMorbidityConditionChecker("dea0c584-32a3-493a-be95-66588e01181a");
        const isHypothyroidism = pregnancyInducedMorbidityConditionChecker("1e527e8b-76ef-426d-aee7-a9002dc031d0");
        const isSyphillis = pregnancyInducedMorbidityConditionChecker("d9414d88-e0cf-4ffe-9cba-151d6879b773");
        const isOtherPremorbidity = pregnancyInducedMorbidityConditionChecker("9f771423-6205-42db-8428-293ac0ad9b13");

        // Comparing the current ANC weight with the previous ANC weight. If the weight has not increased by 2 kg or more, it is considered high risk.
        let isInadequareWeightGain = false;
        const earliestDate = moment(programEncounter.earliestVisitDateTime);
        const ancPreviousEncounters = programEncounter.programEnrolment.getEncounters(true).filter((enc) => enc.encounterType.name === 'ANC' && enc.encounterDateTime && earliestDate.isAfter(moment(enc.earliestVisitDateTime)));
        if (ancPreviousEncounters.length > 0) {
            ancPreviousEncounters.sort((a, b) => moment(b.earliestVisitDateTime).diff(moment(a.earliestVisitDateTime)));
            const lastANCEncounter = ancPreviousEncounters[0];

            const currentWeight = programEncounter.getObservationReadableValue("3981ddb0-30a3-43d2-9564-16ae9cc0e25e");
            const lastMonthWeight = lastANCEncounter.getObservationReadableValue("3981ddb0-30a3-43d2-9564-16ae9cc0e25e");

            if (lastMonthWeight + 2 > currentWeight) {
                isInadequareWeightGain = true;
            }
        }

        // Array to hold true conditions and their corresponding values
        const highRiskConditions = [
            {isTrue: isSevereAnemia, value: "Severe anemia"},
            {isTrue: isModerateAnemia, value: "Moderate anemia"},
            {isTrue: isMideAnemia, value: "Mild anemia"},
            {isTrue: isInadequareWeightGain, value: "Inadequate Weight Gain during Pregnancy"},
            {isTrue: isHypertension, value: "Hypertension"},
            {isTrue: isDiabetes, value: "Diabetes/ Gestational Diabetes"},
            {isTrue: isTuberculosis, value: "Tuberculosis"},
            {isTrue: isAsthma, value: "Asthma"},
            {isTrue: isPreeclampsia, value: "Pre-eclampsia"},
            {isTrue: isSickleCellAnemia, value: "Sickle cell anemia"},
            {isTrue: isThalassemia, value: "Thalassemia"},
            {isTrue: isHeartConditions, value: "Heart conditions"},
            {isTrue: isHypothyroidism, value: "Hypothyroidism"},
            {isTrue: isSyphillis, value: "Syphillis"},
            {isTrue: isOtherPremorbidity, value: "Other pre-morbidity conditions"}
        ];

        // Array to store high-risk values found in this ANC Encounter
        let clinicalHighRiskValues = [];

        // Retrieve high-risk values found in this ANC Encounter
        highRiskConditions.forEach(condition => {
            if (condition.isTrue) {
                clinicalHighRiskValues.push(condition.value);
            }
        });

        // Retrieve the existing high-risk values.
        let existingHighRiskValues = programEncounter.programEnrolment.getObservationReadableValue('9a7f284b-251d-459b-97d9-929ed280b3d3') || [];

        // Remove the high-risk values found in Previous ANC encounters from existingHighRiskValues and updating it with the new high-risk values from the current ANC high-risk values.
        if (existingHighRiskValues) {
            existingHighRiskValues = existingHighRiskValues.filter(value => !ancHighRiskValues.includes(value));
            clinicalHighRiskValues = clinicalHighRiskValues.concat(existingHighRiskValues);
        }

        return clinicalHighRiskValues;
    }

    // Get latest high-risk conditions
    const clinicalHighRiskValues = getHighRiskConditions();

    decisions.enrolmentDecisions.push({name: "High risk condition", value: clinicalHighRiskValues});
    decisions.encounterDecisions.push({name: "High risk condition", value: clinicalHighRiskValues});

    // Get the latest ANC or Delivery encounter
    const latestEnc = programEncounter.programEnrolment.lastFulfilledEncounter("ANC", "Delivery");

    // Check if the current encounter is the latest one
    const isCurrentEncounterTheLatest = latestEnc === undefined || programEncounter.uuid === latestEnc.uuid || moment(programEncounter.encounterDateTime).isAfter(moment(latestEnc.encounterDateTime));

    if (isCurrentEncounterTheLatest) {

        const currentlyActiveInProgram = !programEncounter.programEnrolment.programExitDateTime;
        const isDeliveryEncounterIncomplete = !programEncounter.programEnrolment.hasCompletedEncounterOfType('Delivery');
        const requiresMedicalInterventionTreatment = new imports.rulesConfig.RuleCondition({programEncounter}).when.latestValueInEntireEnrolment("35f880ca-60b5-4240-97e1-813c0a7a78c4").containsAnswerConceptName("8ebbf088-f292-483e-9084-7de919ce67b7").matches();
        const anmRecommendedMedicalFacilityIntervention = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("f7cc4fd5-5c66-4695-b5e3-1d83c3621f5d").containsAnswerConceptName("8ebbf088-f292-483e-9084-7de919ce67b7").matches();
        const isSevereAnemic = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("68bc6e51-eb49-4816-b78b-2427bbab8d92").lessThan(7).matches();
        const isGeographicalHighRisk = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("96b167e1-2d98-40b9-af04-8e4f64f9999a").containsAnswerConceptName("8ebbf088-f292-483e-9084-7de919ce67b7").matches();

        if(currentlyActiveInProgram && isDeliveryEncounterIncomplete && (requiresMedicalInterventionTreatment || anmRecommendedMedicalFacilityIntervention || isSevereAnemic)) {

            const toBeMonitoredByQRT = (isGeographicalHighRisk || anmRecommendedMedicalFacilityIntervention || isSevereAnemic);
            const value = toBeMonitoredByQRT ? "Yes" : "No";

            decisions.registrationDecisions.push({name: "To be monitored by QRT", value});
            decisions.enrolmentDecisions.push({name: "To be monitored by QRT", value});
            decisions.encounterDecisions.push({name: "To be monitored by QRT", value});
        }
        else{
            decisions.registrationDecisions.push({name: "To be monitored by QRT", value: "No"});
            decisions.enrolmentDecisions.push({name: "To be monitored by QRT", value: "No"});
            decisions.encounterDecisions.push({name: "To be monitored by QRT", value: "No"});
        }
        if (isGeographicalHighRisk) {
            decisions.enrolmentDecisions.push({name: "Pregnancy geographically high risk", value: "Yes"});
        }
        if (clinicalHighRiskValues.length > 0) {
            decisions.enrolmentDecisions.push({name: "Clinically high risk", value: "Yes"});
        }
    } else {
        decisions.encounterDecisions.push({name: "To be monitored by QRT", value: "No"});
    }

    return decisions;
};

